import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useUsers } from '../context/UserContext';
import { useForm } from 'react-hook-form';

const CreateUser = () => {
  const navigate = useNavigate();
  const { createUser, loading } = useUsers();
  const { register, handleSubmit, formState: { errors }, reset } = useForm();

  const onSubmit = async (data) => {
    try {
      const formattedData = {
        ...data,
        lat: parseFloat(data.lat),
        lng: parseFloat(data.lng)
      };
      await createUser(formattedData);
      navigate('/');
    } catch (error) {
      console.error('Error creating user:', error);
    }
  };

  return (
    <div className="create-user">
      <div className="create-user-header">
        <Link to="/" className="back-link">
          ← Back to Dashboard
        </Link>
        <h1>Create New User</h1>
      </div>

      <div className="create-user-content">
        <form onSubmit={handleSubmit(onSubmit)} className="user-form">
          <div className="form-section">
            <h3>Personal Information</h3>

            <div className="form-group">
              <label className="form-label">Full Name</label>
              <input
                {...register('name', {
                  required: 'Name is required',
                  minLength: { value: 2, message: 'Name must be at least 2 characters' }
                })}
                className={`form-input ${errors.name ? 'error' : ''}`}
                placeholder="Enter full name"
              />
              {errors.name && <span className="form-error">{errors.name.message}</span>}
            </div>

            <div className="form-group">
              <label className="form-label">Email</label>
              <input
                type="email"
                {...register('email', {
                  required: 'Email is required',
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: 'Invalid email address'
                  }
                })}
                className={`form-input ${errors.email ? 'error' : ''}`}
                placeholder="Enter email address"
              />
              {errors.email && <span className="form-error">{errors.email.message}</span>}
            </div>

            <div className="form-group">
              <label className="form-label">Phone</label>
              <input
                {...register('phone', {
                  required: 'Phone is required',
                  minLength: { value: 10, message: 'Phone must be at least 10 digits' }
                })}
                className={`form-input ${errors.phone ? 'error' : ''}`}
                placeholder="Enter phone number"
              />
              {errors.phone && <span className="form-error">{errors.phone.message}</span>}
            </div>

            <div className="form-group">
              <label className="form-label">Company</label>
              <input
                {...register('company', {
                  required: 'Company is required',
                  minLength: { value: 2, message: 'Company name must be at least 2 characters' }
                })}
                className={`form-input ${errors.company ? 'error' : ''}`}
                placeholder="Enter company name"
              />
              {errors.company && <span className="form-error">{errors.company.message}</span>}
            </div>
          </div>

          <div className="form-section">
            <h3>Address Information</h3>

            <div className="form-group">
              <label className="form-label">Street Address</label>
              <input
                {...register('street', {
                  required: 'Street address is required',
                  minLength: { value: 5, message: 'Street address must be at least 5 characters' }
                })}
                className={`form-input ${errors.street ? 'error' : ''}`}
                placeholder="Enter street address"
              />
              {errors.street && <span className="form-error">{errors.street.message}</span>}
            </div>

            <div className="form-row">
              <div className="form-group">
                <label className="form-label">City</label>
                <input
                  {...register('city', {
                    required: 'City is required',
                    minLength: { value: 2, message: 'City must be at least 2 characters' }
                  })}
                  className={`form-input ${errors.city ? 'error' : ''}`}
                  placeholder="Enter city"
                />
                {errors.city && <span className="form-error">{errors.city.message}</span>}
              </div>

              <div className="form-group">
                <label className="form-label">Zipcode</label>
                <input
                  {...register('zipcode', {
                    required: 'Zipcode is required',
                    minLength: { value: 3, message: 'Zipcode must be at least 3 characters' }
                  })}
                  className={`form-input ${errors.zipcode ? 'error' : ''}`}
                  placeholder="Enter zipcode"
                />
                {errors.zipcode && <span className="form-error">{errors.zipcode.message}</span>}
              </div>
            </div>
          </div>

          <div className="form-section">
            <h3>Geo Location</h3>

            <div className="form-row">
              <div className="form-group">
                <label className="form-label">Latitude</label>
                <input
                  type="number"
                  step="any"
                  {...register('lat', {
                    required: 'Latitude is required',
                    min: { value: -90, message: 'Latitude must be between -90 and 90' },
                    max: { value: 90, message: 'Latitude must be between -90 and 90' }
                  })}
                  className={`form-input ${errors.lat ? 'error' : ''}`}
                  placeholder="Enter latitude"
                />
                {errors.lat && <span className="form-error">{errors.lat.message}</span>}
              </div>

              <div className="form-group">
                <label className="form-label">Longitude</label>
                <input
                  type="number"
                  step="any"
                  {...register('lng', {
                    required: 'Longitude is required',
                    min: { value: -180, message: 'Longitude must be between -180 and 180' },
                    max: { value: 180, message: 'Longitude must be between -180 and 180' }
                  })}
                  className={`form-input ${errors.lng ? 'error' : ''}`}
                  placeholder="Enter longitude"
                />
                {errors.lng && <span className="form-error">{errors.lng.message}</span>}
              </div>
            </div>
          </div>

          <div className="form-actions">
            <button
              type="button"
              onClick={() => reset()}
              className="btn btn-secondary"
              disabled={loading}
            >
              Reset
            </button>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'Please wait...' : 'Create User'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateUser;
