import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useUsers } from '../../context/UserContext';

const UserCard = ({ user }) => {
  const { deleteUser } = useUsers();
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);

  const handleDelete = async () => {
    try {
      await deleteUser(user.id);
    } catch (error) {
      console.error('Error deleting user:', error);
    }
    setShowDeleteDialog(false);
  };

  return (
    <>
      <div className="user-card">
        <div className="user-card-header">
          <h3 className="user-name">
            <Link to={`/users/${user.id}`} className="user-link">
              {user.name}
            </Link>
          </h3>
          <div className="user-actions">
            <Link to={`/edit/${user.id}`} className="action-btn edit-btn">
              Edit
            </Link>
            <button 
              onClick={() => setShowDeleteDialog(true)}
              className="action-btn delete-btn"
            >
              Delete
            </button>
          </div>
        </div>

        <div className="user-card-content">
          <div className="user-info">
            <p className="user-email">{user.email}</p>
            <p className="user-phone">{user.phone}</p>
            <p className="user-company">{user.company}</p>
          </div>

          <div className="user-address">
            <p className="address-text">
              {user.address.street}, {user.address.city} {user.address.zipcode}
            </p>
          </div>
        </div>

        <div className="user-card-footer">
          <Link to={`/users/${user.id}`} className="view-details-btn">
            View Details →
          </Link>
        </div>
      </div>

      {showDeleteDialog && (
        <div className="dialog-backdrop" onClick={() => setShowDeleteDialog(false)}>
          <div className="confirm-dialog danger">
            <div className="dialog-header">
              <h3>Delete User</h3>
              <button onClick={() => setShowDeleteDialog(false)} className="dialog-close">
                ×
              </button>
            </div>

            <div className="dialog-content">
              <p>Are you sure you want to delete {user.name}? This action cannot be undone.</p>
            </div>

            <div className="dialog-actions">
              <button onClick={() => setShowDeleteDialog(false)} className="btn btn-secondary">
                Cancel
              </button>
              <button onClick={handleDelete} className="btn btn-danger">
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default UserCard;
